create table  emailinfo
(
       emailId           NUMBER(10) not null,
       emailUID          VARCHAR2(22),
       sender            VARCHAR2(50),
       recipients        VARCHAR2(1000),
       copyRecipients    VARCHAR2(1000),
       secretRecipients  VARCHAR2(1000),
       title             VARCHAR2(100),
       content           VARCHAR2(1000),
       thetime           VARCHAR2(20),
       upload            VARCHAR2(300),
       emailType         VARCHAR2(1),
       emailStatus       VARCHAR2(1),
       priority          VARCHAR2(4),
       replySign         VARCHAR2(5),
       isupload          VARCHAR2(5),
       emailSize         VARCHAR2(30)
);
alter  table emailinfo
       add constraint PK_emailinfo_emailId primary key (emailId);
